# supporter
type s : t {
    predicates {
        on(o, s);
        cleaned(s);
        dirty(s);
    }
    rules {
        clean/s :: $at(P, r) & $at(s, r) & dirty(s) -> cleaned(s);
        stain/s :: $at(P, r) & $at(s, r) & cleaned(s) -> dirty(s);
    }
    reverse_rules {
        clean/s :: stain/s;
    }
    constraints {
        s1 :: cleaned(s) & dirty(s) -> fail();
    }

    inform7 {
        type {
            kind :: "supporter";
            definition :: "supporters are fixed in place. supporter can be cleaned or dirty.";
        }

        predicates {
            on(o, s) :: "The {o} is on the {s}";
            cleaned(s) :: "The {s} is cleaned";
            dirty(s) :: "The {s} is dirty";
        }
        commands {
            clean/s :: "clean {s}" :: "cleaning the {s}";
            stain/s :: "stain {s}" :: "staining the {s}";
        }
        code :: """

            [Cleaning supporter]
            Understand the command "clean" as something new.
            Cleaning is an action applying to one thing.
            Understand "clean [something]" as cleaning.

            Carry out cleaning:
                if the noun is cleaned:
                    say "[the noun] is already cleaned.";
                    stop;
                Now the noun is cleaned;
                say "You clean [the noun].";

            [Staining supporter]
            Understand "stain [something]" as staining.
            Staining is an action applying to one thing.

            Carry out staining a supporter:
                if the noun is dirty:
                    say "[noun] is already dirty.";
                    stop;
                Now the noun is dirty;
                say "You stain [the noun].";
            """;
    }
}

# stove
type stove : s {
    predicates {
        turned_on(stove);
        turned_off(stove);
    }
    rules {
        turn_on/stove :: $at(P, r) & $at(stove, r) & turned_off(stove) -> turned_on(stove);
        turn_off/stove :: $at(P, r) & $at(stove, r) & turned_on(stove) -> turned_off(stove);

        cook/stove/burned :: $at(P, r) & $at(stove, r) & $in(f, I) & cooked(f) & edible(f) & turned_on(stove) -> burned(f) & inedible(f);
        cook/stove/cooked/raw :: $at(P, r) & $at(stove, r) & $in(f, I) & raw(f) & turned_on(stove) -> cooked(f);
    }
    reverse_rules {
        turn_on/stove :: turn_off/stove;
    }
    constraints {
        stove1 :: turned_on(stove) & turned_off(stove) -> fail();
    }
    inform7 {
        type {
            kind :: "stove-like";
            definition :: "stove-like can be turned on or turned off.";
        }
        predicates {
            turned_on(stove) :: "The {stove} is turned on";
            turned_off(stove) :: "The {stove} is turned off";
        }
        commands {
            turn_on/stove :: "turn on {stove}" :: "turning on the {stove}";
            turn_off/stove :: "turn off {stove}" :: "turning off the {stove}";

            cook/stove/cooked/raw :: "cook {f} with {stove}" :: "cooking the {f} with the {stove}";
            cook/stove/burned :: "cook {f} with {stove}" :: "cooking the {f} with the {stove}";
        }
        code :: """

            [Turning on stove]
            Understand the command "turn" as something new.
            Turning on is an action applying to one thing.
            Understand "turn on [something]" as turning on.

            Carry out turning on:
                if the noun is turned on:
                    say "[the noun] is already on.";
                    stop;
                Now the noun is turned on;
                say "You turned on [the noun].";

            [Turning off stove]
            Understand "turn off [something]" as turning off.
            Turning off is an action applying to one thing.

            Carry out turning off a stove-like:
                if the noun is turned off:
                    say "[noun] is already off.";
                    stop;
                Now the noun is turned off;
                say "You turned off [the noun].";

            [Cooking food
            Understand the command "cook" as something new.
            Understand "cook [something] with [something]" as cooking it with.
            cooking it with is an action applying to a food and a stove.

            Carry out cooking a food with a stove:
                if a stove-like (called stove) is turned on:
                    if a food (called food item) is cooked:
                        Now the food item is burned;
                        Now the food item is inedible;
                        say "the food item is burned.";
                    otherwise:
                        Now the food item is cooked;
                        Now the food item is edible;
                        say "the food item is cooked.";
                otherwise:
                    say "The stove is not on";]
        """;
    }
}
